import {React, useContext, useEffect, useState} from 'react';
import './jumbotron.css';
import {Button, Card, Form, InputGroup} from 'react-bootstrap';
import { MyContext } from '../../context';

function AppJumbotron() {
  const [searchInput, setSearchInput] = useState("");
  const [jokes, setJokes] = useState("");
  const {setMeals} = useContext(MyContext);

  useEffect(() => {
    fetch(`https://api.spoonacular.com/food/trivia/random?apiKey=15c64f05d6ff4e6d987979d29ba03d63`)
    .then((res) => res.json())
    .then((data) => {setJokes(data.text);})
    .catch(error => console.log(error));
  }, []);
  
  // search API based on user type input
  function handleSearch(){
    fetch(`https://www.themealdb.com/api/json/v1/1/search.php?s=${searchInput}`)
    .then((res) => res.json())
    .then((res) => setMeals(res))
    .catch(error => console.log(error));
  }

  // call handleSearch function if "enter" key is pressed
  const handleKeypress = e => {
  if (e.key === "Enter") {
    handleSearch();
  }
};

  return (
    <div className='jumbotron'>
      <h1>Welcome</h1>
      <h2>Find your favourite recipes here!</h2>
      <Card body>{jokes}</Card>;
      <div className='search-input'>
        <InputGroup className="mb-3" size='lg'>
          <Form.Control
            placeholder="Search recipe..."
            aria-label="Recipe Search Input"
            aria-describedby="search-recipe-button"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
            onKeyPress={handleKeypress}
          />
          <Button className='search-button' variant='link' type="submit" id="search-recipe-button" onClick={handleSearch}>
            Search
          </Button>
        </InputGroup>
      </div>
    </div>
  );
}

export default AppJumbotron;
