import {React, useContext, useState} from 'react';
import {Button, Modal} from 'react-bootstrap';
import { MyContext } from '../../context';
import './mealmodal.css';

function MealModal({title, description, idMeal}) {
  const [show, setShow] = useState(false);
  const {user, setUser} = useContext(MyContext);
  const [loading, setLoading] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const handleAddToFavorites = async() => {
    setLoading(true);
    const response = await fetch('http://localhost:5000/add-favorites', {
      method:'POST',
      headers: {
        'x-access-token': localStorage.getItem('token'),
      },
      body: JSON.stringify({
        mealId: idMeal
    })
    })
      const data = await response.json();
      setLoading(false);
      setUser(data);
      alert('Added to favorites successfully');
  };

  const handleRemoveFromFavorites = async() => {
    setLoading(true);
    const response = await fetch('http://localhost:5000/remove-favorites', {
      method:'POST',
      headers: {
        'x-access-token': localStorage.getItem('token'),
      },
      body: JSON.stringify({
        mealId: idMeal
    })
    })
    const data = await response.json();
      setLoading(false);
      setUser(data);
      alert('Removed from favorites successfully');
  };

  return (
    <>
      <Button className='modal-button mb-3' variant="link" size='lg' onClick={handleShow}>View More
      </Button>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{description}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          {user && (
            <>
            {user.favorites.includes(idMeal) ? (
            <Button variant="danger" onClick={handleRemoveFromFavorites} disabled={loading}>Remove from Favorites</Button>
            ) : ( 
            <Button className='modal-button' variant="primary" onClick={handleAddToFavorites} disabled={loading}>
            Add to Favorites
            </Button>
            )}
            </>
          )}
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default MealModal;
